use analytics;

CREATE TABLE `leads` (
  `id` int AUTO_INCREMENT PRIMARY KEY NOT NULL,
  `traffic_channel` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci,
  `quiz_answers1` text,
  `quiz_answers2` text,
  `quiz_answers3` text,
  `quiz_answers4` text,
  `quiz_answers5` text,
  `quiz_answers6` text,
  `status_amo` varchar(255) DEFAULT NULL,
  `payment_amount` decimal(10,2) DEFAULT NULL,
  `date_request` timestamp NULL DEFAULT NULL,
  `date_payment` timestamp NULL DEFAULT NULL,
  `date_status_change` timestamp NULL DEFAULT NULL,
  `opener` varchar(255) DEFAULT NULL,
  `closer` varchar(255) DEFAULT NULL,
  `current_lead_amo` varchar(255) DEFAULT NULL,
  `main_lead_amo` varchar(255) DEFAULT NULL,
  `is_double` varchar(255) DEFAULT NULL,
  `email` varchar(255) DEFAULT NULL,
  `phone` varchar(20) DEFAULT NULL,
  `channel_expense` decimal(10,2) DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) DEFAULT CHARSET=utf8mb4;

CREATE TABLE accounts (
    id INTEGER PRIMARY KEY AUTO_INCREMENT,
    label VARCHAR(255) UNIQUE NOT NULL,
    title VARCHAR(255) UNIQUE NOT NULL,
    trafficologist_id INTEGER NOT NULL
);

CREATE TABLE trafficologists (
    id INTEGER PRIMARY KEY AUTO_INCREMENT,
    name VARCHAR(255) UNIQUE NOT NULL
);

CREATE TABLE users (
    id INTEGER PRIMARY KEY AUTO_INCREMENT,
    login VARCHAR(255) NOT NULL UNIQUE,
    password VARCHAR(255) NOT NULL
);

CREATE TABLE sessions (
    id INTEGER PRIMARY KEY AUTO_INCREMENT,
    user_id INTEGER NOT NULL,
    token VARCHAR(255) NOT NULL
);

CREATE TABLE target_audience_filters (
    id INTEGER PRIMARY KEY AUTO_INCREMENT,
    user_id INTEGER NOT NULL,
    pickle VARCHAR(255) NOT NULL
);

